import React from "react";
import Square from "./Square";

const GameBoard = ({ currentPlayer, handleSquareClick }) => {
  const renderSquare = (i) => {
    return <Square id={i} currentPlayer={currentPlayer} onClick={handleSquareClick} />;
  };

  return (
    <div className="game-board">
      <div className="grid-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="grid-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="grid-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
};

export default GameBoard;
