import React, { useState, useEffect } from "react";
import "./tictactoe.css";

const PLAYER_X = "X";
const PLAYER_O = "O";

const Board = ({ players }) => {
  const [player, setPlayer] = useState(PLAYER_X);
  const [gameState, setGameState] = useState([]);

  const checkForWinner = (gameState) => {
    // ...
  };

  useEffect(() => {
    const winner = checkForWinner(gameState);
    if (winner) {
      console.log(`We have a winner: ${winner}`);
    }
  }, [gameState]);

  const takeTurn = (id) => {
    setGameState([...gameState, { id: id, player: player }]);
    setPlayer(player === PLAYER_X ? PLAYER_O : PLAYER_X); // toggle player
  };

  const renderSquare = (i) => <Square takeTurn={takeTurn} id={i} />;

  return (
    <div className="game-board">
      <div className="grid-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="grid-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="grid-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
      <div id="info">
        <h1 id="turn">Next Player: {players[player]}</h1>
      </div>
    </div>
  );
};

const Square = ({ takeTurn, id }) => {
  const mark = [PLAYER_O, PLAYER_X];
  const [tik, setTik] = useState(null);

  const onClick = () => {
    if (!tik) {
      const nextPlayer = takeTurn(id);
      setTik(nextPlayer);
      console.log(`Square: ${id} filled by player : ${nextPlayer}`);
    }
  };

  return (
    <button className={tik === PLAYER_X ? "red" : "white"} onClick={onClick}>
      <h1>{mark[tik]}</h1>
    </button>
  );
};

const TicTacToe = () => {
  const [players, setPlayers] = useState(null);

  useEffect(() => {
    const playerX = prompt("Please enter player X's name");
    const playerO = prompt("Please enter player O's name");
    setPlayers({ PLAYER_X: playerX, PLAYER_O: playerO });
  }, []);

  if (!players) {
    return null;
  }

  return (
    <div className="game">
      <h1>React Tic-tac-toe</h1>
      <Board players={players} />
    </div>
  );
};

export default TicTacToe;
