import React, { useState, useEffect } from "react";
import GameBoard from "./GameBoard";
import GameScore from "./GameScore";
import "./tictactoe.css";

const PLAYER_X = "X";
const PLAYER_O = "O";

const TicTacToe = () => {
  const [players, setPlayers] = useState(null);
  const [currentPlayer, setCurrentPlayer] = useState(PLAYER_X);
  const [gameState, setGameState] = useState(Array(9).fill(null));
  const [winner, setWinner] = useState(null);

  useEffect(() => {
    const checkForWinner = () => {
      // ... implement your checkForWinner logic here ...
    };

    const winner = checkForWinner();
    if (winner) {
      setWinner(winner);
      console.log(`We have a winner: ${winner}`);
    }
  }, [gameState]);

  const takeTurn = (id) => {
    if (gameState[id] || winner) {
      return;
    }

    const updatedGameState = [...gameState];
    updatedGameState[id] = currentPlayer;
    setGameState(updatedGameState);

    setCurrentPlayer(currentPlayer === PLAYER_X ? PLAYER_O : PLAYER_X);
  };

  const handlePlayerNamesSubmit = (playerX, playerO) => {
    setPlayers({ PLAYER_X: playerX, PLAYER_O: playerO });
  };

  return (
    <div className="game">
      {!players && (
        <PlayerInput onSubmit={handlePlayerNamesSubmit} />
      )}

      {players && (
        <div>
          <GameScore
            players={players}
            currentPlayer={currentPlayer}
            winner={winner}
          />
          <GameBoard gameState={gameState} takeTurn={takeTurn} />
        </div>
      )}
    </div>
  );
};

const PlayerInput = ({ onSubmit }) => {
  const [playerX, setPlayerX] = useState("");
  const [playerO, setPlayerO] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(playerX, playerO);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Player X name:
        <input
          type="text"
          value={playerX}
          onChange={(e) => setPlayerX(e.target.value)}
        />
      </label>
      <label>
        Player O name:
        <input
          type="text"
          value={playerO}
          onChange={(e) => setPlayerO(e.target.value)}
        />
      </label>
      <input type="submit" value="Start Game" />
    </form>
  );
};

export default TicTacToe;
