import React from "react";

const GameScore = ({ players, currentPlayer, winner }) => {
  const renderPlayerName = (player) =>
    players[player] ? players[player] : `Player ${player}`;

  const renderNextPlayer = () => {
    if (winner) {
      return `Winner: ${renderPlayerName(winner)}`;
    }
    return `Next Player: ${renderPlayerName(currentPlayer)}`;
  };

  return (
    <div className="game-score">
      <h1>{renderNextPlayer()}</h1>
    </div>
  );
};

export default GameScore;
